var selectedPin;
var latAvg = 0;
var lngAvg = 0;
var count = 0;
var mapPins = {};

/**
 * Initialise script state (run once at startup)
 */
Script.on('load', function() {

	ClientEvents.setOptions({
		persist:false
	});

	ClientEvents.subscribe("backPressed", function(data) {
		Client.jumpToScreen("Traps Phone");
	});

	selectedPin = Script.getState("currentPin");
});

Script.on('server', function(data, channel) {

	if (data.sysmeta.label == "sensacollection" && typeof data.value == "string") {
		var collection = SensaCollection.load(JSON.parse(data.value));

		var mapData = collection.filter(['instance','LAT', 'LON','STATE']);

		if (mapData.columns.length !== 4) {
			Log.info("Data is out of date. Could not locate sensor positions.");
			return;
		}

		mapData.setColumns(['instance', 'lat', 'lon', 'state']);
		var pinTemplate = Script.getScriptElement("pinTemplateMobile");

		mapData.forEach(function(result, key) {
			var pinContent = pinTemplate.cloneNode(true);
			pinContent.querySelector("[data-value='title']").innerHTML = key;
			pinContent.querySelector("[data-value='id']").innerHTML = key;

			if (selectedPin && selectedPin.toLowerCase() == key.toLowerCase()) {
				pinContent.classList.add("pinSelected");
				ClientEvents.publish("setMap", {
					lat: parseFloat(result.lat),
					lng: parseFloat(result.lon)
				});			
			}

			// Set pin state.
			var state = +result.state;
			if (state && state == 1) {
				if (channel.split("/")[1].toLowerCase() === "virtual") {
					pinContent.querySelector("[data-value='icon']").classList.add("virtual");
				}
				pinContent.querySelector("[data-value='icon']").classList.add("greenIcon");
			} else if (state && state == 2) {
				pinContent.querySelector("[data-value='icon']").classList.add("redIcon");
			}

			var pin = {
				id: key,
				content: pinContent,
				loc: {
					lat: parseFloat(result.lat),
					lng: parseFloat(result.lon)
				}
			};

			mapPins[key.toLowerCase()] = pin;

			if (typeof state !== "undefined" && state == 0) {
				return;
			}

			// Send pin to map.
			ClientEvents.publish("mapPins", pin, false);

			latAvg += pin.loc.lat;
			lngAvg += pin.loc.lng;
			count += 1;
		});

		if (selectedPin == null) {
			ClientEvents.publish("setMap", {
				lat: latAvg / count,
				lng: lngAvg / count
			});
		}
	} else {
		// Get channel id
		var fqn = data.sysmeta.channel.split("/");
		var id = fqn[2];
		var scope = fqn[3].toLowerCase();

		if (scope === "state" && data.value == 2) {
			Client.notify("Trapper Triggered", {
				body: id + " was triggered.",
				icon: "/userfiles/Animal-Trap-Icon-Red.png"
			});
			if (mapPins[id.toLowerCase()] !== undefined) {
				mapPins[id.toLowerCase()].content.querySelector("[data-value='icon']").classList = [];
				mapPins[id.toLowerCase()].content.querySelector("[data-value='icon']").classList.add('redIcon');
				mapPins[id.toLowerCase()].content.style.setProperty("display", "");
				// Send pin to map.
				ClientEvents.publish("mapPins", mapPins[id.toLowerCase()], false);
			}
		} else	if (scope === "state" && data.value == 1) {
			if (mapPins[id.toLowerCase()] !== undefined) {
				mapPins[id.toLowerCase()].content.querySelector("[data-value='icon']").classList = [];
				mapPins[id.toLowerCase()].content.querySelector("[data-value='icon']").classList.add('greenIcon');
				mapPins[id.toLowerCase()].content.style.setProperty("display", "");
				// Send pin to map.
				ClientEvents.publish("mapPins", mapPins[id.toLowerCase()], false);
			}
		} else if (scope === "state" && data.value == 0) {
			if (mapPins[id.toLowerCase()] !== undefined) {
				mapPins[id.toLowerCase()].content.querySelector("[data-value='icon']").classList = [];
				mapPins[id.toLowerCase()].content.querySelector("[data-value='icon']").classList.add('redIcon');
				ClientEvents.publish("remove pin", id, false);
			}
		}

		if (scope === "lat") {
				// Get pin and update location
				var pin = mapPins[id.toLowerCase()];

				if (pin !== undefined) {
					pin.loc.lat = parseFloat(data.value);
					ClientEvents.publish("mapPins", pin);
					mapPins[id] = pin;
				}
			} else if (scope === "lon") {
				// Get pin and update location
				var pin = mapPins[id.toLowerCase()];

				if (pin !== undefined) {
					pin.loc.lng = parseFloat(data.value);
					ClientEvents.publish("mapPins", pin);
					mapPins[id] = pin;
				}
			}
	}
});

