		element.setAttribute('href', "../userfiles/photo_processor/" + img.account + "/" + camname + "/" + vid_file);
	} else {
		element.setAttribute('href', "../userfiles/photo_processor/" + img.account +  "/" + camname + "/" + filename + ".JPG");
