// Handle sidebar and pins.
Script.on("client", function(eventData) {   
    // Center map
    var pinName = eventData.value.toLowerCase();
    var pinDetails = pins[pinName];
    eventData.value = pinDetails.value.loc;  
    // Open pin on side.
    var rowDetails = rows[pinName];
    rowDetails.openClearScreen(true);
});

Script.on("server", function(eventData) {
    if (eventData.usrmeta.func !== "READRECS" || loaded == true) {
        return;   
    }
    // Database 
    var data = eventData.value.data;
    var keys = Object.keys(data);
    var packet = {};
    packet.value = {};

    for (var i in Object.keys(data)) {
        var k = keys[i];
        var val = data[k];
        var device = "Trap " + k;
        if (Script.getState("cards")[device] == undefined) {
            // Create row for device TODO address lookup and set time.
            var trapType = val[0];
            var state = parseInt(val[1]);
            var lat = parseFloat(val[4]);
            var lng = parseFloat(val[3]);
            var street = val[5];
            var suburb = val[6].toUpperCase();
            var trapper = val[7].toUpperCase();
            var setTime = undefined;
            var hours = 0;

            if (val[8] !== undefined) {
                if (val[8] === -1) {
                    setTime = new Date();   
                } else {
                    setTime = new Date(parseInt(val[8])); 
                }
                hours = diffInHours(setTime, new Date());
            }

            var entry = createRowEntry(device, street, suburb, hours, trapType);
            var info = {
                ID: k,
                TrapType: trapType,
                State: state,
                Lng: lng,
                Lat: lat,
                Street: street,
                Suburb: suburb,
                Trapper: trapper.toLowerCase(),
                SetTime: setTime.getTime()
            };

            entry.info = info; 
            var pinPacket; 

            if (suburb === "" || street === "") {
               // entry.reverseLookup(lat, lng);    
            }

            switch (state) {
                case 0: 
                    pinPacket = createPopupPacket(device, deviceNicknames[device], lat, lng, "grey");
                    entry.setState("inactive", undefined, false);                     
                    break;
                case 1:
                    pinPacket = createPopupPacket(device, deviceNicknames[device], lat, lng, "green");
                    entry.setState("set", hours, false);
                    break;
                case 2:
                    pinPacket = createPopupPacket(device, deviceNicknames[device], lat, lng, "red");
                    entry.setState("triggered", undefined, false);
                    break;
            }

            packet.value[device] = [entry];
            rows[device.toLowerCase()] = entry;
            pins[device.toLowerCase()] = pinPacket;
        }
    }

    Script.setState("pins", pins);  

    var data = packet.value;
    var payload = {};
    payload.label = "sensacollection";
    payload.value = {};
    payload.value.data = data;
    payload.value.headers = ["page"];

    Script.sendToWidget("page", "receive value", payload.value, "sensacollection");
    var sortedArray = sortTable();
    updateHours();
    Script.setState("cards", rows);
    rows[sortedArray[0].toLowerCase()].openClearScreen();
    setInterval(updateHours, hourRefresh);
    Script.setState("tableLoaded", true);
    loaded = true;
});

function sortTable() {
     // Sort table
    var sortedRowArray = Object.values(rows).sort(function(a, b) {
        if (a.info.State !== b.info.State) {            
            return b.info.State - a.info.State;
        } else {
            return a.info.SetTime - b.info.SetTime;
        }
    });

    var sortedArray = sortedRowArray.map(function(item) {
        return "Trap " + item.info.ID;    
    }); 

    Script.setState("sortedArray", sortedArray); 
    Script.sendToWidget("page", "sort row by array", sortedArray);
    return sortedArray;
}

// Update hours
function updateHours() {    
    var keys = Object.keys(rows);
    for (var i = 0; i < keys.length; i++) {
        var row = rows[keys[i]];
        if (row.info.State == 0) {
        }
        var hours = row.querySelector("[data-info='hours']");
        // Calc new hours
        var setTime = row.info.SetTime;
        if (setTime === undefined) {
            continue;
        }

        if (row.info.State == 0) {
            hours.innerHTML = "";
        } else {
            hours.innerHTML = diffInHours(new Date(setTime), new Date());            
        }
        //row.updateStats("settime", setTime);
    }
}


function createPopupPacket(id, trapNum, lat, lng, color) {
    var packet = {
        value: {
            id: trapNum.toLowerCase(),
            loc: {
                lat: lat,
                lng: lng
            },
            content: createPopup(id, trapNum, color)
        }
    };
    pins[trapNum.toLowerCase()] = packet;
    return packet;
}


