// Update the stats of a card. It takes an object.
function createUpdateStatsFunction(item) {
    return function(stat, value) {
        // Loop over object and update according
        var setTime = item.querySelector("[data-stat='setTime']");
        var updateTime = item.querySelector("[data-stat='updateTime']");
        var temp = item.querySelector("[data-stat='temp']");
        var battery = item.querySelector("[data-stat='battery']");
        var signal = item.querySelector("[data-stat='signal'");
        var suburb = item.querySelector("[data-info='suburb']");
        var street = item.querySelector("[data-info='address']");

        switch (stat.toLowerCase()) {            
            case "temp":
                temp.innerHTML = value + "Â°C";
                break;
            case "settime":
                var date = new Date(value).toLocaleString('en-GB');
                var d = date.split(", ");
                var d0 = d[0].split("/");
                d0[2] = d0[2] - 2000;
                d0 = d0.join("/");
                if (item.info.State === 0) {
                    setTime.innerHTML = "-";
                } else {
                    setTime.innerHTML = d[1] + "<br />" + d0;
                }
                updateHours();
                break;
            case "battery":
                switch (value) {
                    case 1:
                        //value = "0 - 10%";
                        value = "<b style='color: red';>Very Low</b>";
                        break;
                    case 2:
                        //value = "10 - 25%";
                        value = "Low";
                        break;
                    case 3:
                        //value = "25 - 40%";
                        value = "Medium"
                        break;
                    case 4:
                        //value = "40% +";
                        value = "Full"
                        break;
                }
                battery.innerHTML = value;
                break;
            case "signal":
                // No signal transmitted
                if (value == 99) {
                    signal.innerHTML = "<b style='color: red';>--</b>";
                    break;
                }

                value = Math.round(value * 3.123);

                if (value < 35) {
                    signal.innerHTML = "<b style='color: red';>" + value + "%</b>";
                } else {
                    signal.innerHTML = value + "%";
                }
                break;
            case "street":
                street.innerHTML = value;
                break;
            case "suburb":
                suburb.innerHTML = value;
                break;
            // Default update time
            case "pulse":
                var date = new Date(value).toLocaleString('en-GB');
                var d = date.split(", ");
                var d0 = d[0].split("/");
                d0[2] = d0[2] - 2000;
                d0 = d0.join("/");

                updateTime.innerHTML = d[1] + "<br />" + d0;
                updateHours();
        }
    };
}

function createPopup(id, title, color) {
    var item = Script.getScriptElement("popup2");
    item.children[1].setAttribute("data-name", id.toLowerCase());
    var titleElem = item.querySelector("[data-info='title'");
    var iconElem = item.querySelector("[data-info='icon']");
    titleElem.innerHTML = title;    
    iconElem.classList = [];
    iconElem.classList.add("" + color + "Icon");
    iconElem.classList.add("vertical-center");
    return item;
}

// Creates row entry
function createRowEntry(title, address, suburb, hours, trapType) {
        var item = Script.getScriptElement("card").cloneNode(true);          
        var main = item.querySelector("[data-section='main']");
        var titleElem = item.querySelector("[data-info='title']");
        var addressElem = item.querySelector("[data-info='address']");
        var suburbElem = item.querySelector("[data-info='suburb']");
        var hourElem = item.querySelector("[data-info='hours'");  
        var trapTypeElem = item.querySelector("[data-info='animalType']");
        // Set id
        item.id = title;
        // Update title tag
        titleElem.innerHTML =  deviceNicknames[title];
        // Update address
        addressElem.innerHTML = address;
        // Update Suburb
        suburbElem.innerHTML = suburb;
        // Update hours
        hourElem.innerHTML = hours;
        // Trap Type
        trapTypeElem.innerHTML = trapType;   
        main.style.setProperty("border-right", "");        
        // Add events to show panels on select.
        item.openClearScreen = createClearFunction(item);
        item.hideClearScreen = createHideClearFunction(item);
        item.openFormScreen = createFormFunction(item);
        item.hideFormScreen = createHideFormFunction(item);
        item.setIconColor = createIconFunction(item);
        item.setState = createSetStateFunction(item);
        item.disableButtonFunction = disableButtonFunction(item);
        item.submitButtonFunction = submitButtonFunction(item);
        item.updateStats = createUpdateStatsFunction(item);
        item.setTrapType = createTrapTypeFunction(item);
        item.setTrapper = createTrapperFunction(item);
        item.jump = createJumpFunction(item);


        item.children[1].querySelector("[data-section='cell']").onclick = item.openClearScreen;
        item.children[1].querySelector("[data-button='clearButton']").onclick = item.openFormScreen;
        item.children[1].querySelector("[data-button='cancelButton']").onclick = item.hideFormScreen;
        item.children[1].querySelector("[data-button='deactivateButton']").onclick = item.disableButtonFunction;
        item.children[1].querySelector("[data-button='submitButton']").onclick = item.submitButtonFunction;
        item.children[1].querySelector("[data-info='suburb']").onclick = item.jump;

        item.info = {};
    return item;
}

function createTrapTypeFunction(item) {
    return function(value) {
        var trapElem = item.querySelector("[data-info='animalType']");
        trapElem.innerHTML = value;
        item.info.TrapType = value;
    };
}

function createTrapperFunction(item) {
    return function(value) {
        var trapperElem = item.querySelector("[data-info='trapperName']");
        trapperElem.innerHTML = value;
        item.info.Trapper = value;
    };
}

function createIconFunction(item) {
    return function(color) {
        var icon = item.querySelector("[data-info='icon']");
        switch (color) {
            case "grey":
                icon.classList = [];
                icon.classList.add("greyIcon");                
                break;
            case "green":
                icon.classList = [];
                icon.classList.add("greenIcon");
                break;
            case "red":
                icon.classList = [];
                icon.classList.add("redIcon");
                break;
        }
    };
}

function disableButtonFunction(item) {
    return function() {
        item.openFormScreen();
        var trapTypeSelect = item.querySelector("[data-select='trapType']");
        var animalTypeSelect = item.querySelector("[data-select='animalType']");
        item.querySelector("[data-button='submitButton']").innerHTML = "DEACTIVATE";
        item.querySelector("[data-button='submitButton']").style.setProperty("background-color", "#333333");
        trapTypeSelect.setAttribute("disabled", "disabled");
        trapTypeSelect.style.setProperty("background-color", "lightgray");
        trapTypeSelect.style.setProperty("background-image", "none");       
        animalTypeSelect.setAttribute("disabled", "disabled");
        animalTypeSelect.style.setProperty("background-color", "lightgray");
        animalTypeSelect.style.setProperty("background-image", "none");
    };
}

function submitButtonFunction(item) {
    return function() {
        var animalSelect = item.querySelector("[data-select='animalType']");
        var trapperSelect = item.querySelector("[data-select='trapper']");
        var trapTypeSelect = item.querySelector("[data-select='trapType']");
        var button = item.querySelector("[data-button='submitButton']");
        var buttonText = item.querySelector("[data-button='submitButton']").innerHTML.trim();
        if (buttonText === "DEACTIVATE" && 
            trapperSelect.value.indexOf("select") !== -1) {
                alert("Please select a trapper.");
            return;
        } else if (buttonText !== "DEACTIVATE") {
            if (animalSelect.value.indexOf("select") !== -1 && item.info.State != 0) {
               alert("Please select an animal.");
               return;
            } else if (trapperSelect.value.indexOf("select") !== -1) {
               alert("Please select a trapper.");
               return;
            } else if (trapTypeSelect.value.indexOf("select") !== -1) {
               alert("Please select a trap type.");
               return;
            }
        }

        switch(item.querySelector("[data-button='submitButton']").innerHTML.trim()) {
            case "CLEAR":
                var packet = {
                    value: "0"
                };
                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/ACTIVATED", packet);
                item.setState("inactive");
                break;
            case "SET":
            case "RESET":
                item.info.TrapType = trapTypeSelect.value;
                item.info.Trapper = trapperSelect.value;
                item.info.SetTime = new Date().getTime();
                var packet = {
                    value: "1"
                };
                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/ACTIVATED", packet);
                packet.value = trapTypeSelect.value;
                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/TRAPTYPE", packet);
                packet.value = trapperSelect.value;
                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/TRAPPER", packet);
                item.setState("set");
                var d = new Date().getTime();
                item.updateStats("setTime", d);
                item.info.SetTime = d;
                var packet = {
                    value: d.toString()
                };
                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/SETTIME", packet);
                break;
            case "DEACTIVATE":
                button.style.setProperty("background-color", "");
                var trapTypeSelect = item.querySelector("[data-select='trapType']");
                var animalTypeSelect = item.querySelector("[data-select='animalType']");
                trapTypeSelect.setAttribute("disabled", "");
                animalTypeSelect.setAttribute("disabled", "");
                var packet = {
                    value: "0"
                };

                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/ACTIVATED", packet);
                packet.value = "";
                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/TRAPTYPE", packet);
                Script.System.publishToChannel("KG/BRISBANE/" + item.info.ID + "/TRAPPER", packet);
                item.setState("inactive");
                break;
        }
    };
}

var previousPin;
function createClearFunction(item) {
  return function(center) {
      var button = item.querySelector("[data-button='clearButton'");
      var disableButton = item.querySelector("[data-button='deactivateButton'");
      var main = item.querySelector("[data-section='main']");
      button.style.setProperty("visibility", "visible");
      // Close current     
      tableDom = Script.getWidgetDOM("page");
      var nodes = tableDom.querySelectorAll("[data-section='main']");
      var current = item.children[1];
      if (item.info.State === 2 || item.info.State === 0) {
          disableButton.style.setProperty("visibility", "none");
      } else {
          disableButton.style.setProperty("visibility", "visible");   
      }

      // TODO remove loops
      var current = -1;
      for (var i = 0; i < nodes.length; i++) {
          var n = nodes[i];
          n.style.setProperty("border-right", "");
          // reset everything to false
          if (main == n) {
              current = i;
              continue;
          };
          n.parentElement.parentElement.Open = false;
      }

      nodes = tableDom.querySelectorAll("[data-section='clearScreen']");
      for (var i = 0; i < nodes.length; i++) {    
          var n = nodes[i];
          n.style.setProperty("display", "none");
      }

      nodes = tableDom.querySelectorAll("[data-section='formScreen']");
      for (var i = 0; i < nodes.length; i++) {    
          var n = nodes[i];
          n.style.setProperty("display", "none");
      }

      // Change pin background
      if (previousPin) {
          previousPin.value.content.classList.remove("pinSelected");   
      }

      // Exit if it was already open as it has now been closed.
      if (item.Open) {
          item.Open = false;
          return;
      } else {
          item.Open = true;
      }

      var section = item.children[1].querySelector("[data-section='clearScreen']");
      section.style.setProperty("display", "block");
      main.style.setProperty("border-right", "5px solid black");
      if (mode == modes.DESKTOP) {
          // Center map
          var pinName = item.id.toLowerCase();
          var pinDetails = pins[pinName];
          if (!pinDetails) {
              return;
          }
          var packet = {
              value: pinDetails.value.loc
          };      
          if (center !== true) {
              Script.sendToWidget("pinMap", "set map", packet.value, "object");
          }     

          // Set buttons to state
          // save pin to revert later
          pinDetails.value.content.classList.add("pinSelected");
          previousPin = pinDetails;
      }
  };
}

