// Returns the difference between two dates in hours.
// 36e5 = 60*60*1000, dividing by which converts the milliseconds difference into hours.
function diffInHours(date1, date2) {
    var hours = Math.floor(Math.abs(date1 - date2) / (60 * 60 * 1000));   
    if (isNaN(hours)) {
       return "-";   
    }
    return hours;
}

