/**
 * Response to message from server channel
 */
var firstLoadLat = false;
var firstLoadLng = false;
var firstLoad = true;
Script.on('server', function(eventData) { 
    // Lock server until other is loaded
    // Update card

    if (eventData.label == "sensacollection") {
        var packet = JSON.parse(eventData.value);
        var channel = eventData.sysmeta.channel;

        // Send to self for processing
        var headers = packet.headers;
        var keys = Object.keys(packet.data);
        for (var i = 0; i < keys.length; i++) {
            for (var j = 0; j < headers.length; j++) {

                if (headers[j] == "instance") {
                    continue;
                }
                var info = packet.data[keys[i]][j]; 
                var newChannel = channel;
                newChannel = newChannel.split("/");
                newChannel[2] = keys[i];
                newChannel[3] = headers[j];
                newChannel = newChannel.join("/");
                var sys = {};
                sys.channel = newChannel;
                var event = {};
                event.sysmeta = sys;
                event.value = {};
                event.value = info;
                serverEvent(event);
            }
        }
        return;    
    }

    var cards = Script.getState("cards");
    var fqn = eventData.sysmeta.channel.split("/");
    var id = fqn[2];
    var scope = fqn[3].toLowerCase();    
    var card = cards["trap " + id];
    // No card found in db
    if (card == undefined) return;

    /*
    if (eventData.date === undefined) {
        eventData.date = new Date().getTime();    
    }
    */
    card.updateStats(scope, Math.round(eventData.value));

    if (scope === "activated") {
        /*
        if (eventData.date === undefined) {
            eventData.date = new Date().getTime();    
        } else {
            eventData.date = new Date(eventData.date).getTime();   
        }

        if (eventData.date - card.info.SetTime > 0) {
            card.updateStats("setTime", eventData.date);
            card.info.SetTime = eventData.date;
            */
            //if (card.info.State == 1 & eventData.value == 2) {
            if (eventData.value == 2) {
                if (mode == modes.DESKTOP) {
                    var p = Script.getState("pins")["trap " + id];
                    var icon = p.value.content.querySelector("[data-info='icon']");
                    icon.classList = [];
                    icon.classList.add("redIcon");
                }
                card.setState("triggered");
            } else if (eventData.value == 1) {
                if (mode == modes.DESKTOP) {
                    var p = Script.getState("pins")["trap " + id];
                    var icon = p.value.content.querySelector("[data-info='icon']");
                    icon.classList = [];
                    icon.classList.add("greenIcon");
                    var packet = Script.getState("pins")["trap " + id];
                }
                card.setState("set");
            } else if (eventData.value == 0) {
                if (mode == modes.DESKTOP) {
                    var p = Script.getState("pins")["trap " + id];
                    var icon = p.value.content.querySelector("[data-info='icon']");
                    icon.classList = [];
                    icon.classList.add("greyIcon");
                }
                card.setState("inactive");
            }         
       // }
    }

    if (scope === "traptype") {
        card.setTrapType(eventData.value);    
    }

    if (scope === "trapper") {
        card.setTrapper(eventData.value);   
    }

    if (scope === "settime" && card.info.State != 0) {
        card.info.SetTime = +eventData.value;
        card.updateStats("settime", +eventData.value);    
    }

    if (scope === "suburb") {
        card.updateStats("suburb", eventData.value);    
    }

    if (scope === "street") {
        card.updateStats("street", eventData.value);    
    }

    if ((scope === "lat" || scope === "lng")) {
       var pins = Script.getState("pins");
       var p = pins["trap " + id];
        if (scope === "lat") {
            p.value.loc.lat = parseFloat(eventData.value);
            firstLoadLat = true;
        } else {
            p.value.loc.lng = parseFloat(eventData.value);
            firstLoadLng = true;
        }
        if (card.info.State != 0) {
            //Script.sendToWidget("pinMap", "add custom popup", p);
            if ("Trap " + id == Script.getState("sortedArray")[0] && (firstLoadLat && firstLoadLng) && firstLoad) {
                //Script.sendToWidget("pinMap", "set map", { value: p.value.loc });
                firstLoad = false;
            }
        }
    }

    console.log(eventData);
});

