function sortTable() {
     // Sort table
    var sortedRowArray = Object.values(rows).sort(function(a, b) {
        if (a.info.State !== b.info.State) {            
            return b.info.State - a.info.State;
        } else {
            return a.info.SetTime - b.info.SetTime;
        }
    });

    var sortedArray = sortedRowArray.map(function(item) {
        return "Trap " + item.info.ID;    
    }); 

    Script.setState("sortedArray", sortedArray); 
    Script.sendToWidget("page", "sort row by array", sortedArray);
}

// Update hours
function updateHours() {    
    var keys = Object.keys(rows);
    for (var i = 0; i < keys.length; i++) {
        var row = rows[keys[i]];
        if (row.info.State == 0) {
        }
        var hours = row.querySelector("[data-info='hours']");
        // Calc new hours
        var setTime = row.info.SetTime;
        if (setTime === undefined) {
            continue;
        }

        if (row.info.State == 0) {
            hours.innerHTML = "";
        } else {
            hours.innerHTML = diffInHours(new Date(setTime), new Date());            
        }
        row.updateStats("settime", setTime);
    }
}


