// Update the stats of a card. It takes an object.
function createUpdateStatsFunction(item) {
    return function(stat, value) {
        // Loop over object and update according
        var setTime = item.querySelector("[data-stat='setTime']");
        var updateTime = item.querySelector("[data-stat='updateTime']");
        var temp = item.querySelector("[data-stat='temp']");
        var battery = item.querySelector("[data-stat='battery']");
        var signal = item.querySelector("[data-stat='signal'");
        var suburb = item.querySelector("[data-info='suburb']");
        var street = item.querySelector("[data-info='address']");

        switch (stat.toLowerCase()) {            
            case "temp":
                temp.innerHTML = value + "Â°C";
                break;
            case "settime":
                var date = new Date(value).toLocaleString('en-GB');
                var d = date.split(", ");
                var d0 = d[0].split("/");
                d0[2] = d0[2] - 2000;
                d0 = d0.join("/");
                if (item.info.State === 0) {
                    setTime.innerHTML = "-";
                } else {
                    setTime.innerHTML = d[1] + "<br />" + d0;
                }
                break;
            case "battery":
                switch (value) {
                    case 1:
                        //value = "0 - 10%";
                        value = "<b style='color: red';>Very Low</b>";
                        break;
                    case 2:
                        //value = "10 - 25%";
                        value = "Low";
                        break;
                    case 3:
                        //value = "25 - 40%";
                        value = "Medium"
                        break;
                    case 4:
                        //value = "40% +";
                        value = "Full"
                        break;
                }
                battery.innerHTML = value;
                break;
            case "signal":
                if (value < 35) {
                    signal.innerHTML = "<b style='color: red';>" + value + "%</b>";
                } else {
                    signal.innerHTML = value + "%";
                }
                break;
            case "street":
                street.innerHTML = value;
                break;
            case "suburb":
                suburb.innerHTML = value;
                break;
            // Default update time
            case "pulse":
                var date = new Date(value).toLocaleString('en-GB');
                var d = date.split(", ");
                var d0 = d[0].split("/");
                d0[2] = d0[2] - 2000;
                d0 = d0.join("/");

                updateTime.innerHTML = d[1] + "<br />" + d0;
                updateHours();
        }
    };
}

