// Creates row entry
function createRowEntry(title, address, suburb, hours, trapType) {
        var item = Script.getScriptElement("card").cloneNode(true);          
        var main = item.querySelector("[data-section='main']");
        var titleElem = item.querySelector("[data-info='title']");
        var addressElem = item.querySelector("[data-info='address']");
        var suburbElem = item.querySelector("[data-info='suburb']");
        var hourElem = item.querySelector("[data-info='hours'");  
        var trapTypeElem = item.querySelector("[data-info='animalType']");
        // Set id
        item.id = title;
        // Update title tag
        titleElem.innerHTML = title;
        // Update address
        addressElem.innerHTML = address;
        // Update Suburb
        suburbElem.innerHTML = suburb;
        // Update hours
        hourElem.innerHTML = hours;
        // Trap Type
        trapTypeElem.innerHTML = trapType;   
        main.style.setProperty("border-right", "");        
        // Add events to show panels on select.
        item.openClearScreen = createClearFunction(item);
        item.hideClearScreen = createHideClearFunction(item);
        item.openFormScreen = createFormFunction(item);
        item.hideFormScreen = createHideFormFunction(item);
        item.setIconColor = createIconFunction(item);
        item.setState = createSetStateFunction(item);
        item.disableButtonFunction = disableButtonFunction(item);
        item.submitButtonFunction = submitButtonFunction(item);
        item.updateStats = createUpdateStatsFunction(item);
        item.reverseLookup = createReverseLookup(item);
        item.setTrapType = createTrapTypeFunction(item);
        item.setTrapper = createTrapperFunction(item);

        item.children[1].querySelector("[data-section='cell']").onclick = item.openClearScreen;
        item.children[1].querySelector("[data-button='clearButton']").onclick = item.openFormScreen;
        item.children[1].querySelector("[data-button='cancelButton']").onclick = item.hideFormScreen;
        item.children[1].querySelector("[data-button='deactivateButton']").onclick = item.disableButtonFunction;
        item.children[1].querySelector("[data-button='submitButton']").onclick = item.submitButtonFunction;

        item.info = {};
    return item;
}

function createTrapTypeFunction(item) {
