/**
 * Initialise script state (run once at startup)
 */
var tableLoaded = false;
var backlog = [];

Script.on('load', function() {
    Script.System.subscribeToChannel("kg/brisbane/+/activated");
    Script.System.subscribeToChannel("kg/brisbane/+/lat");
    Script.System.subscribeToChannel("kg/brisbane/+/lng");
});

/**
 * Response to message from server channel
 */
Script.on('server', function(eventData) { 
    // Lock server until other is loaded
    debugger;
    var fqn = eventData.sysmeta.channel.split("/");
    var id = fqn[2];
    var scope = fqn[3].toLowerCase();
    var saveToCards = false;
    var cards;
    var pins;
    var card;

    if (Script.getState("cards") && Script.getState("cards")["trap " + id]) {
        saveToCards = true;
        card = Script.getState("cards")["trap " + id];
    }

    if (Script.getState("pins") === null) {
        Script.setState("pins", {});  
    }

    pins = Script.getState("pins");
    if (pins["trap " + id] == null) {       
        var p = createPopupPacket("trap " + id);
        pins["trap " + id] = p;
    }


    if (eventData.date === undefined) {
        eventData.date = new Date().getTime();    
    }
    if (scope === "activated") {      
        if (eventData.value == 2) {
            var p = Script.getState("pins")["trap " + id];
            var icon = p.value.content.querySelector("[data-info='icon']");
            icon.classList = [];
            icon.classList.add("redIcon");
        } else if (eventData.value == 1) {
            var p = Script.getState("pins")["trap " + id];
            var icon = p.value.content.querySelector("[data-info='icon']");
            icon.classList = [];
            icon.classList.add("greenIcon");
         } else if (eventData.value == 0) {
            var p = Script.getState("pins")["trap " + id];
            var icon = p.value.content.querySelector("[data-info='icon']");
            icon.classList = [];
            icon.classList.add("greyIcon");
         }         
    }

    if (scope === "lat" || scope === "lng") {
       var pins = Script.getState("pins");
       var p = pins["trap " + id];
        if (scope === "lat") {
            p.value.loc.lat = parseFloat(eventData.value);
            if (saveToCards)
                card.info.Lat = parseFloat(eventData.value);     
        } else {
            p.value.loc.lng = parseFloat(eventData.value);
            if (saveToCards)
                card.info.Lng = parseFloat(eventData.value);     
        }
        Script.sendToWidget("pinMap", "add custom popup", p.value);
    }

    if (pins[Script.getState("selectedPin")] != undefined) {
        var p = pins[Script.getState("selectedPin")];
        Script.sendToWidget("pinMap", "add custom popup", p.value);
    }


});

function createPopupPacket(trapNum, lat, lng, color) {
    var packet = {
        value: {
            id: trapNum.toLowerCase(),
            loc: {
                lat: lat,
                lng: lng
            },
            content: createPopup(trapNum, color)
        }
    };
    Script.getState("pins")[trapNum.toLowerCase()] = packet;
    return packet;
}

function createPopup(title, color) {
    var item = Script.getScriptElement("popup");
    item.children[1].setAttribute("data-name", title.toLowerCase());
    var titleElem = item.querySelector("[data-info='title'");
    var iconElem = item.querySelector("[data-info='icon']");
    titleElem.innerHTML = title;    
    iconElem.classList = [];
    iconElem.classList.add("" + color + "Icon");
    iconElem.classList.add("vertical-center");
    return item;
}

/**
 * Response to message on client channel
 */
Script.on('client', function(eventData) {
    Script.System.jumpToScreen("Traps");
});
